import 'package:flutter/material.dart';import 'package:flutter/cupertino.dart';void main() {  runApp(const MyApp());}class MyApp extends StatelessWidget {  const MyApp({super.key});  // This widget is the root of your application.  @override  Widget build(BuildContext context) {    return MaterialApp(      title: '플러터 스터디',      theme: ThemeData(          fontFamily: 'sunflower',          textTheme: TextTheme(            headline1: TextStyle(              color: Colors.black,              fontSize: 80.0,              fontWeight: FontWeight.w700,              fontFamily: 'parisiene',            ),            headline2: TextStyle(              color: Colors.blue,              fontSize: 50.0,              fontWeight: FontWeight.w700,            ),            bodyText1: TextStyle(              color: Colors.blue,              fontSize: 30.0,            ),          )      ),      home: homeScreen(),    );  }}// homescreen 생성class homeScreen extends StatefulWidget {  const homeScreen({Key? key}) : super(key:key);  @override  State<homeScreen> createState() {    return _HomeScreenState();  }}// 상위 위쳇class _DDay extends StatelessWidget {  // StatelessWidget : final처럼 값이 안 변하게 해주는 위젯  final GestureTapCallback onHeartPressed;  final DateTime firstDay;  _DDay({    required this.onHeartPressed,    required this.firstDay,  });  @override  Widget build(BuildContext context) {    final textTheme =Theme.of(context).textTheme;    final now = DateTime.now();    return Column(      children: [        const SizedBox(height: 16.0),        Text(          'U&I',          style: textTheme.headline1,        ),        const SizedBox(height: 16.0),        Text(          '우리 처음 만난 날',          style: textTheme.bodyText1,        ),        Text(          '${firstDay.year}.${firstDay.month}.${firstDay.day}',          style: textTheme.bodyText2,        ),        const SizedBox(height: 16.0),        IconButton(iconSize:60.0, onPressed: onHeartPressed,            icon: Icon(Icons.favorite, color: Colors.red)),        const SizedBox(height: 16.0),        Text(          'D+${DateTime(now.year, now.month, now.day)              .difference(firstDay).inDays +1}',          style: textTheme.headline2,        ),      ],    );  }}// 바텀 위젯class _CoupleImage extends StatelessWidget {  @override  Widget build(BuildContext context) {    return Expanded(      child : Center(        child: Image.asset(          'assets/middle_img.png',          // 화면 반만큼 높이 구현          height: MediaQuery.of(context).size.height / 2,        ),      ),    );  }}// 홈스크린 상태관리class _HomeScreenState extends State<homeScreen>{  DateTime firstDay = DateTime.now();  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.pink[100],      body: SafeArea(        //자식 위젯에 패딩을 넣어서 디바이스 영역이 앱의 위젯 영역을 침범하는 것을 막아준다        top: true,        bottom: false,        child: Column(          // 위아래 끝에 위젯 배치          mainAxisAlignment: MainAxisAlignment.spaceBetween,          // 반대축 최대 크기로 늘리기          crossAxisAlignment: CrossAxisAlignment.stretch,          children: [            _DDay(              // 하트 클릭시 이벤트              onHeartPressed : onHeartPressed,              firstDay : firstDay,            ),            _CoupleImage(),          ],        ),      ),    );  }  void onHeartPressed() {    showCupertinoDialog(context: context, builder: (BuildContext context){      return Align(        alignment: Alignment.bottomCenter,        child: Container(          color: Colors.white,          height: 300,          child: CupertinoDatePicker(            mode: CupertinoDatePickerMode.date,            onDateTimeChanged: (DateTime date){              setState((){                firstDay = date;              });            },          ),        ),      );    },      barrierDismissible: true,    );  }}